import React, {useState} from 'react';
import './App.css';

import Card from './view/Card/Card'

function App() {
  let names = [];
  const [infoList, setInfoList] = useState('');

  return (
    <div className="App">

          <form onSubmit = {getInfo}>
            <input type = "text" name = "name" placeholder = "enter name"  />
            <input type='number' name='age' placeholder='Enter your age' />
            {/* <input type = "text" age = "age" placeholder = "enter age" /> */}
            <input type = "submit" value = "ok"/>
          </form>   

          <h>{infoList}</h>

      </div>

  );
  function setList(e){ //event listener
    names.map((data, index) =>{
      return (<Card data = {data} key = {index}/>)
    })
    //setInfoList(e[])
  }

  function getInfo(e){ //event listener
    e.preventDefault();
    let info = { //recieves the name input
      name: e.target.elements.name.value,
      age: e.target.elements.age.value
    }
    names.push(info) //adds the new info to the names list
    setList(e);
    // saveInputs(info);
  }
  function saveInputs(e){
    names.push(e.target.elements.name.value) //adds the name to a list of names

    setInfoList(names)
 }


}





export default App;
